mapper.select:
select count(id) from question_t where use_yn!='n'
and creation_user = #{creationUser}
and question_content like concat('%', #{questionContent}, '%')

select id, question_type_id as questionTypeId, question_content as questionContent, question_type as questionType,
from question_t where use_yn!='n'
and creation_user = #{creationUser}
and question_content like concat('%', #{questionContent}, '%')
limit #{pageIndex},#{pageSize}
