mapper.select:
select id, title, status_id as statusId, active_date_start as activeDateStart, active_date_end as activeDateEnd,
from questionaire_t where use_yn != 'n'
and creation_user = #{creationUser}
and title like concat('%', #{title}, '%')
limit #{pageIndex},#{pageSize}

select count(id) from questionaire_t where use_yn != 'n'
and creation_user = #{creationUser}
and title like concat('%', #{title}, '%')

mapper.delete:
update questionaire_t set
use_yn='n' 
where id=#{id}

mapper.update:
update questionaire_t set
title=#{title, javaType=string, jdbcType=VARCHAR},
status_id=#{statusId},
lastupdate_timestamp=#{lastupdateTimestamp},
lastupdate_user=#{lastupdateUser, javaType=INTEGER, jdbcType=INTEGER}
where id=#{id}
